const callout='<blockquote class="twitter-tweet center-block" data-lang="[sys-locale]"><p lang="[tweet-locale]" dir="ltr">[replace-text]<\/p>&mdash; [tweet-name] ([screen-name]) <a class="tweet-time" href="https://twitter.com/[screen-name]/status/[tweet-id]" data-time="[tweet-time]"><\/a><\/blockquote>';var address=window.location.protocol+"//"+window.location.host,details={resource:(window.location.pathname.split("/").slice(0,-1).join("/")+"/socket.io").substring(1)},socket=io.connect(address,details);socket.on("tweet",function(n){let i=n.user,t=$("#tweets");t.prepend(callout.replace(/\[replace-text\]/g,n.text).replace(/\[tweet-name\]/g,i.name).replace(/\[screen-name\]/g,i.screen_name).replace(/\[tweet-id\]/g,n.id_str).replace(/\[tweet-locale\]/g,n.lang).replace(/\[tweet-time\]/g,n.created_at).replace(/\[sys-locale\]/g,$("#culture-input").val()));twttr.widgets.load(t);t.find("a.tweet-time").each(function(){$(this).text(DateFormat.format.prettyDate($(this).data("time")))});let r=$("#tweets > .twitter-tweet");r.length>5&&r.last().remove()});