const callout='<div class="bs-callout bs-callout-warning tweets-content"><img style="float: left; margin:8px" src="[profile-pic]"><b>[tweet-name]<\/b> - <span data-time="[tweet-time]" class="tweet-time"><\/span><p>[replace-text]<\/p><\/div>';var socket=io.connect("https://socket.savejaparipark.com");socket.on("tweet",function(n){let t=n.user,i=$("#tweets");i.prepend(callout.replace("[replace-text]",n.text).replace("[tweet-name]",t.name).replace("[tweet-time]",n.created_at).replace("[profile-pic]",t.profile_image_url_https));i.find("span.tweet-time").each(function(){$(this).text(DateFormat.format.prettyDate($(this).data("time")))});let r=$("#tweets > .tweets-content");r.length>5&&r.last().remove()});